select a.idx as ch_idx, b.category_name as ch_name from tb_stb_schedule a left join tb_live_category b on a.category_idx = b.idx where a.category_idx in
#{type}
and date_format(start, '%Y-%m-%d') = #{date} and del_flag = "N"

select a.idx as sch_idx, a.name as sch_name, a.desc_text as sch_desc, date_format(a.start, '%Y-%m-%d %H:%i:%s') as sch_start, date_format(a.end, '%Y-%m-%d %H:%i:%s') as sch_end, a.live_stream_url as sch_stream_url, a.image_path as sch_thumbnail, a.caption as sch_subtitle, a.source_type from tb_stb_schedule a left join tb_live_category b on a.category_idx = b.idx where a.category_idx in
#{type}
and a.idx = #{ch_idx} and date_format(a.start, '%Y-%m-%d') = #{date} and a.del_flag = "N"
